import React, { useState, useEffect } from 'react';
import { findUserById, updateUser } from '../api';
import "../styles/profile.css";

const Profile = ({ userId }) => {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    email: '',
    phoneNumber: '',
    dateOfBirth: '',
    photo: null,
  });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        console.log(u)
        const response = await findUserById(userId);
        const userData = response.data;
        setFormData({
          name: userData.name,
          address: userData.address,
          email: userData.email,
          phoneNumber: userData.phoneNumber,
          dateOfBirth: userData.dateOfBirth,
          photo: null, // Assuming the photo URL is not provided here
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, photo: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updateData = { ...formData };
      if (formData.photo) {
        // Handle file upload logic here if necessary
        // For example, upload the photo to a server and get the URL
        // updateData.photoUrl = uploadedPhotoUrl;
      }
      const response = await updateUser(userId, updateData);
      console.log('Profile updated:', response.data);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div className='profile'>
      <h1 className="profile__title">Profile</h1>
      <div className="details__form">
        <p className="profile__desc">
          Update your photo and personal details here!
        </p>
        <form onSubmit={handleSubmit}>
          <div className="form__group">
            <div>
              <label>Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Enter your name"
              />
            </div>
            <div>
              <label>Address</label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
                placeholder="Enter your Address"
              />
            </div>
          </div>

          <div className="form__group">
            <div>
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your e-mail"
              />
            </div>

            <div>
              <label>Phone Number</label>
              <input
                type="text"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
                placeholder="+94********** Format please"
              />
            </div>
          </div>

          <div className="form__group">
            <div>
              <label>Date of Birth</label>
              <input
                type="date"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={handleChange}
              />
            </div>
          </div>

          <div className="form__group">
            <div>
              <label>Your Photo</label>
              <p className="profile-img__desc">
                This will be displayed in your profile
              </p>
              <input
                type="file"
                name="photo"
                onChange={handleFileChange}
              />
            </div>

            <div className="profile__img-btns">
              <button type="button" className="dlt__btn">Delete</button>
              <button type="submit" className="update__btn">Update</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Profile;
